import React, { useState } from "react";
import { View, TextInput, Text, StyleSheet } from "react-native";
import PropTypes from "prop-types";

const PasswordStrengthMeter = ({
  scoreAlgorithm,
  customStrengthLevels = defaultStrengthLevels,
  style,
  onPasswordChange,
}) => {
  const [password, setPassword] = useState("");

  const handlePasswordChange = (text) => {
    setPassword(text);
    onPasswordChange(text); // Notify parent component of the password change
  };

  const score = scoreAlgorithm(password);
  const { label, color, threshold } = getLevelInfo(score, customStrengthLevels);
  const barWidth = `${(score / threshold) * 100}%`;

  return (
    <View style={[styles.container, style]}>
      <TextInput
        style={styles.input}
        placeholder="Enter your password"
        secureTextEntry={true}
        onChangeText={handlePasswordChange}
        value={password}
      />
      <Text style={[styles.label, { color: color }]}>{label}</Text>
      <View
        style={[
          styles.progressBar,
          { backgroundColor: color, width: barWidth },
        ]}
      />
    </View>
  );
};

// Default strength levels
const defaultStrengthLevels = [
  { label: "Very Weak", threshold: 10, color: "red" },
  { label: "Weak", threshold: 30, color: "orange" },
  { label: "Average", threshold: 50, color: "yellow" },
  { label: "Strong", threshold: 70, color: "lightgreen" },
  { label: "Very Strong", threshold: 100, color: "green" },
];

// Function to determine the label and color based on the score
const getLevelInfo = (score, levels) => {
  for (let level of levels) {
    if (score <= level.threshold) {
      return level;
    }
  }
  return levels[levels.length - 1];
};

PasswordStrengthMeter.propTypes = {
  scoreAlgorithm: PropTypes.func.isRequired,
  customStrengthLevels: PropTypes.arrayOf(
    PropTypes.shape({
      label: PropTypes.string,
      threshold: PropTypes.number,
      color: PropTypes.string,
    })
  ),
  style: PropTypes.object,
  onPasswordChange: PropTypes.func.isRequired,
};

PasswordStrengthMeter.defaultProps = {
  customStrengthLevels: defaultStrengthLevels,
  style: {},
};

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    padding: 16,
    borderRadius: 8,
    width: "100%",
  },
  input: {
    height: 30,
    width: "100%",
    borderColor: "gray",
    borderWidth: 1,
    borderRadius: 4,
    paddingHorizontal: 8,
    marginBottom: 8,
  },
  label: {
    marginTop: 5,
    fontWeight: "bold",
  },
  progressBar: {
    height: 10,
    borderRadius: 5,
    marginTop: 8,
    marginBottom: 8,
    backgroundColor: "lightgrey",
    alignSelf: "stretch",
  },
});

export default PasswordStrengthMeter;
