import React from "react";
import { View, Text, StyleSheet } from "react-native";

const PasswordStrengthMeter = ({ score, customStrengthLevels }) => {
  // Define default strength levels if none are provided
  const defaultStrengthLevels = [
    { label: "Very Weak", threshold: 1, color: "red" },
    { label: "Weak", threshold: 3, color: "orange" },
    { label: "Average", threshold: 5, color: "yellow" },
    { label: "Strong", threshold: 7, color: "lightgreen" },
    { label: "Very Strong", threshold: 9, color: "green" },
  ];

  // Use the provided strength levels or the default ones
  const strengthLevels = customStrengthLevels || defaultStrengthLevels;

  // Find the level that the current score falls into
  const level =
    strengthLevels
      .slice()
      .reverse()
      .find((lvl) => score >= lvl.threshold) || strengthLevels[0];
  const previousThreshold =
    strengthLevels[strengthLevels.indexOf(level) - 1]?.threshold || 0;
  const range = level.threshold - previousThreshold;
  const relativeScore = score - previousThreshold;
  const barWidth = range ? `${(relativeScore / range) * 100}%` : "100%";

  console.log(`Score: ${score}, Level: ${level.label}, Bar Width: ${barWidth}`);

  return (
    <View style={styles.container}>
      <View
        style={[
          styles.strengthBar,
          { width: barWidth, backgroundColor: level.color },
        ]}
      />
      <Text style={styles.label}>{level.label}</Text>{" "}
      {/* Feedback words/labels */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: "100%",
    paddingVertical: 5, // Added padding for visual comfort
    borderRadius: 5,
    overflow: "hidden",
    backgroundColor: "#eee", // Background of the bar container for better visibility
    marginTop: 10,
  },
  strengthBar: {
    height: "100%",
    borderRadius: 5, // Rounded corners for the filled part of the bar
  },
  label: {
    marginTop: 5,
    fontWeight: "bold",
    textAlign: "center", // Center label text
  },
});

export default PasswordStrengthMeter;
