import React, { useState, useEffect } from "react";
import { View, Text, StyleSheet } from "react-native";

const PasswordStrengthMeter = ({
  password,
  calculateStrength,
  colorScheme = ["#dddddd", "#ffaaaa", "#ff8888", "#ff6666"], // Default color scheme
  strengthWords = [
    // Default strength words
    { word: "Weak", strength: 0 },
    { word: "Better", strength: 2 },
    { word: "Strong", strength: 4 },
  ],
  barWidth = "100%",
  showStrengthBar = true,
  showFeedbackText = true,
}) => {
  const [strengthScore, setStrengthScore] = useState(0);
  const [strengthLabel, setStrengthLabel] = useState("Weak");

  useEffect(() => {
    const score = calculateStrength
      ? calculateStrength(password)
      : defaultStrengthCalculation(password);
    setStrengthScore(score);
    const wordObj = strengthWords.find((sw) => score >= sw.strength);
    setStrengthLabel(wordObj ? wordObj.word : "Weak");
  }, [password, calculateStrength, strengthWords]);

  const normalizedStrength = Math.min(strengthScore, strengthWords.length - 1);
  const barColor = colorScheme[normalizedStrength] || "#ddd"; // Fallback color if not defined

  const styles = StyleSheet.create({
    container: {
      width: barWidth,
      height: 10,
      backgroundColor: "#eee",
      borderRadius: 5,
      overflow: "hidden",
      marginTop: 10,
    },
    strengthBar: {
      width: `${(normalizedStrength / (strengthWords.length - 1)) * 100}%`,
      height: "100%",
      backgroundColor: barColor,
    },
    label: {
      marginTop: 5,
      fontWeight: "bold",
    },
    feedbackText: {
      marginTop: 5,
      color: "gray",
    },
  });

  return (
    <View>
      {showStrengthBar && (
        <View style={styles.container}>
          <View style={styles.strengthBar} />
        </View>
      )}
      <Text style={styles.label}>Strength: {strengthLabel}</Text>
      {showFeedbackText && (
        <Text style={styles.feedbackText}>
          Tips to improve your password strength.
        </Text>
      )}
    </View>
  );
};

const defaultStrengthCalculation = (password) => {
  let strengthPoints = 0;

  if (!password) {
    return strengthPoints;
  }

  // Strength calculation logic
  if (password.length >= 8) strengthPoints += 2;
  if (password.length >= 12) strengthPoints += 2;
  if (/[a-z]/.test(password)) strengthPoints += 1;
  if (/[A-Z]/.test(password)) strengthPoints += 1;
  if (/\d/.test(password)) strengthPoints += 1;
  if (/[^A-Za-z0-9]/.test(password)) strengthPoints += 2;

  return Math.min(strengthPoints, strengthWords.length - 1); // Match with the number of strengthWords
};

export default PasswordStrengthMeter;
