import React from "react";
import { View, Text, StyleSheet } from "react-native";

const PasswordStrengthMeter = ({ score, strengthLevels }) => {
  // Default strength levels and colors
  const defaultLevels = [
    { label: "Too Short", threshold: 1, color: "gray" },
    { label: "Weak", threshold: 3, color: "red" },
    { label: "Fair", threshold: 5, color: "orange" },
    { label: "Good", threshold: 7, color: "yellow" },
    { label: "Strong", threshold: 9, color: "green" },
  ];

  // Use provided strength levels or defaults
  const levels = strengthLevels || defaultLevels;

  const findLevel = () => {
    let currentLevel = levels[0];
    for (let level of levels) {
      if (score >= level.threshold) {
        currentLevel = level;
      } else {
        break;
      }
    }
    return currentLevel;
  };

  const level = findLevel();
  const barWidth = `${(score / level.threshold) * 100}%`;

  return (
    <View style={styles.container}>
      <View
        style={[
          styles.strengthBar,
          { width: barWidth, backgroundColor: level.color },
        ]}
      />
      <Text style={styles.label}>Strength: {level.label}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: 10,
    borderRadius: 5,
    overflow: "hidden",
    marginTop: 10,
  },
  strengthBar: {
    height: "100%",
  },
  label: {
    marginTop: 5,
    fontWeight: "bold",
  },
});

export default PasswordStrengthMeter;
