import React, { useState } from "react";
import { View, TextInput, ScrollView, Text, StyleSheet } from "react-native";
import PasswordStrengthMeter from "./PasswordStrengthMeter";

const PasswordStrengthMeterTest = () => {
  const [password1, setPassword1] = useState("");
  const [password2, setPassword2] = useState("");
  const [password3, setPassword3] = useState("");

  // Three custom words and thresholds
  const threeLevels = [
    { label: "Weak", threshold: 3, color: "red" },
    { label: "Fair", threshold: 6, color: "yellow" },
    { label: "Strong", threshold: 9, color: "green" },
  ];

  // Five custom words and thresholds
  const fiveLevels = [
    { label: "Very Weak", threshold: 2, color: "red" },
    { label: "Weak", threshold: 4, color: "orange" },
    { label: "Fair", threshold: 6, color: "yellow" },
    { label: "Good", threshold: 8, color: "lightgreen" },
    { label: "Strong", threshold: 10, color: "green" },
  ];

  // Basic strength algorithm
  const basicAlgorithm = (password) => {
    if (password.length < 4) return 1; // Very weak
    if (password.length < 6) return 2; // Weak
    if (password.length < 8) return 3; // Fair
    if (password.length < 10) return 4; // Good
    return 5; // Strong
  };

  // Advanced strength algorithm
  const advancedAlgorithm = (password) => {
    let score = 0;
    if (password.length >= 8) score += 2;
    if (/[A-Z]/.test(password)) score += 2;
    if (/\d/.test(password)) score += 2;
    if (/[^A-Za-z0-9]/.test(password)) score += 2;
    return Math.min(score, 10); // Max score is 10
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.inputContainer}>
        <Text>Three Custom Words with Basic Algorithm</Text>
        <TextInput
          onChangeText={setPassword1}
          value={password1}
          placeholder="Enter your password"
          secureTextEntry
          style={styles.input}
        />
        <PasswordStrengthMeter
          score={basicAlgorithm(password1)}
          strengthLevels={threeLevels}
        />
      </View>

      <View style={styles.inputContainer}>
        <Text>Five Custom Words with Advanced Algorithm</Text>
        <TextInput
          onChangeText={setPassword2}
          value={password2}
          placeholder="Enter your password"
          secureTextEntry
          style={styles.input}
        />
        <PasswordStrengthMeter
          score={basicAlgorithm(password2)}
          strengthLevels={fiveLevels}
        />
      </View>

      {/* Additional configurations can be added similarly */}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
  },
  inputContainer: {
    marginBottom: 20,
  },
  input: {
    borderColor: "gray",
    borderWidth: 1,
    padding: 10,
    marginBottom: 10,
  },
  title: {
    fontWeight: "bold",
    fontSize: 18,
    marginBottom: 10,
  },
});

export default PasswordStrengthMeterTest;
