import React, { useState } from "react";
import { View, TextInput, ScrollView, Text } from "react-native";
import PasswordStrengthMeter from "./PasswordStrengthMeter";

const PasswordStrengthMeterTest = () => {
  const [password1, setPassword1] = useState("");
  const [password2, setPassword2] = useState("");
  const [password3, setPassword3] = useState("");

  const customColors = [
    "#FFC0CB", // Soft Pink
    "#FFA07A", // Light Orange
    "#FFD700", // Vibrant Yellow
    "#32CD32", // Fresh Green
    "#0000FF", // Deep Blue
    "#800080", // Rich Purple
  ];

  const basicStrengthAlgorithm = (password) => {
    return password.length; // Basic example: Length-based strength calculation
  };

  const advancedStrengthAlgorithm = (password) => {
    let strength = 0;
    if (password.length >= 8) strength += 1;
    if (/[A-Z]/.test(password)) strength += 1;
    if (/\d/.test(password)) strength += 1;
    if (/[^A-Za-z0-9]/.test(password)) strength += 1;
    return strength;
  };

  const customStrengthWords = [
    { word: "Too Simple", strength: 0 },
    { word: "Better", strength: 3 },
    { word: "Good", strength: 5 },
    { word: "Strong", strength: 7 },
    { word: "Very Strong", strength: 9 },
    { word: "Extremely Strong", strength: 10 },
  ];

  const versions = [
    {
      title: "Basic Strength Calculation",
      password: password1,
      setPassword: setPassword1,
      calculateStrength: basicStrengthAlgorithm,
    },
    {
      title: "Advanced Strength Calculation",
      password: password2,
      setPassword: setPassword2,
      calculateStrength: advancedStrengthAlgorithm,
    },
    {
      title: "Custom Words and Colors Strength Calculation",
      password: password3,
      setPassword: setPassword3,
      calculateStrength: advancedStrengthAlgorithm,
      strengthWords: customStrengthWords,
      colorScheme: customColors,
    },
  ];

  return (
    <ScrollView>
      {versions.map((version, index) => (
        <View key={index} style={{ marginVertical: 20 }}>
          <Text style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>
            {version.title}
          </Text>
          <TextInput
            onChangeText={(text) => version.setPassword(text)}
            value={version.password}
            placeholder="Enter your password"
            secureTextEntry
            style={{ marginBottom: 10 }}
          />
          <PasswordStrengthMeter
            password={version.password}
            calculateStrength={version.calculateStrength}
            colorScheme={index === 2 ? customColors : undefined} // Apply custom colors only for the third version
            strengthWords={version.strengthWords}
            showFeedbackText={true}
          />
        </View>
      ))}
    </ScrollView>
  );
};

export default PasswordStrengthMeterTest;
