import React, { useState, useEffect } from "react";
import { View, Text, StyleSheet } from "react-native";

const PasswordStrengthMeter = ({
  password,
  colorScheme = [], // Default to empty array if not provided
  strengthWords,
  barWidth = "100%",
  showStrengthBar = true,
  showFeedbackText = false,
}) => {
  // Default colors
  const defaultColors = ["red", "yellow", "green"];

  // Default strength calculation function
  const defaultStrengthCalculation = (password) => {
    if (!password) return 0;
    let strengthPoints = 0;

    if (password.length >= 8) strengthPoints++;
    if (password.length >= 12) strengthPoints++;
    if (/[a-z]/.test(password)) strengthPoints++;
    if (/[A-Z]/.test(password)) strengthPoints++;
    if (/\d/.test(password)) strengthPoints++;
    if (/[^A-Za-z0-9]/.test(password)) strengthPoints++;

    // Normalize to a scale of 0 to 5
    return Math.min(strengthPoints, 5);
  };

  const [strengthScore, setStrengthScore] = useState(
    defaultStrengthCalculation(password)
  );

  // Function to get the appropriate label or word based on strength
  const getStrengthLabel = (score) => {
    if (strengthWords) {
      // Use the custom strengthWords if provided
      const wordObj = strengthWords.find(
        (wordObj) => score >= wordObj.strength
      );
      return wordObj ? wordObj.word : "";
    } else {
      // Use default labels if strengthWords are not provided
      const labels = ["Weak", "Better", "Strong"]; // Default labels if no strengthWords provided
      const normalizedStrength = Math.min(
        Math.max(score, 0),
        labels.length - 1
      );
      return labels[normalizedStrength];
    }
  };

  useEffect(() => {
    // Default strength calculation function
    const newStrengthScore = defaultStrengthCalculation(password);
    setStrengthScore(newStrengthScore);
  }, [password]);

  // Get bar color based on strength
  const barColor = colorScheme[strengthScore] || defaultColors[strengthScore];

  const styles = StyleSheet.create({
    container: {
      width: barWidth,
      height: 10,
      backgroundColor: "#eee",
      borderRadius: 5,
      overflow: "hidden",
      marginTop: 10,
    },
    strengthBar: {
      width: `${(strengthScore / (colorScheme.length - 1)) * 100}%`,
      height: "100%",
      backgroundColor: barColor,
    },
    label: {
      marginTop: 5,
      fontWeight: "bold",
    },
    feedbackText: {
      marginTop: 5,
      color: "gray",
    },
  });

  return (
    <View>
      {showStrengthBar && (
        <View style={styles.container}>
          <View style={styles.strengthBar} />
        </View>
      )}
      <Text style={styles.label}>
        Strength: {getStrengthLabel(strengthScore)}
      </Text>
      {showFeedbackText && (
        <Text style={styles.feedbackText}>
          Tips to improve your password strength.
        </Text>
      )}
    </View>
  );
};

export default PasswordStrengthMeter;
