import React from "react";
import { View, Text, StyleSheet } from "react-native";
import PropTypes from "prop-types";

const PasswordStrengthMeter = ({
  password,
  score,
  customStrengthLevels,
  style,
}) => {
  const defaultStrengthLevels = [
    { label: "Very Weak", threshold: 1, color: "red" },
    { label: "Weak", threshold: 3, color: "orange" },
    { label: "Average", threshold: 5, color: "yellow" },
    { label: "Strong", threshold: 7, color: "lightgreen" },
    { label: "Very Strong", threshold: 9, color: "green" },
  ];

  const levels = customStrengthLevels || defaultStrengthLevels;
  const maxThreshold = levels[levels.length - 1].threshold;

  const getLevelInfo = () => {
    for (let i = 0; i < levels.length; i++) {
      if (score <= levels[i].threshold) {
        return levels[i];
      }
    }
    return levels[levels.length - 1];
  };

  const { label, color } = getLevelInfo();
  const barWidth = `${(score / maxThreshold) * 100}%`;

  return (
    <View style={[styles.container, style]}>
      <View
        style={[
          styles.strengthBar,
          { width: barWidth, backgroundColor: color },
        ]}
      />
      {password && password.length > 0 && (
        <Text style={[styles.label, { color: color }]}>{label}</Text>
      )}
      <View style={styles.labelsContainer}>
        {levels.map((level, index) => (
          <Text
            key={index}
            style={[
              styles.levelLabel,
              { color: label === level.label ? color : "black" },
            ]}
          >
            {level.label}
          </Text>
        ))}
      </View>
    </View>
  );
};

PasswordStrengthMeter.propTypes = {
  password: PropTypes.string.isRequired,
  score: PropTypes.number.isRequired,
  customStrengthLevels: PropTypes.arrayOf(
    PropTypes.shape({
      label: PropTypes.string,
      threshold: PropTypes.number,
      color: PropTypes.string,
    })
  ),
  style: PropTypes.object,
};

PasswordStrengthMeter.defaultProps = {
  customStrengthLevels: null,
  style: {},
};

const styles = StyleSheet.create({
  container: {
    width: "100%",
    marginTop: 10,
  },
  strengthBar: {
    height: 10,
    borderRadius: 5,
    overflow: "hidden",
  },
  label: {
    marginTop: 5,
    fontWeight: "bold",
  },
  labelsContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginTop: 5,
  },
  levelLabel: {
    flex: 1,
    textAlign: "center",
  },
});

export default PasswordStrengthMeter;
