import React from "react";
import { View, Text, StyleSheet } from "react-native";

const PasswordStrengthMeter = ({ score, customColors, customLabels }) => {
  // Default colors and labels if not provided
  const defaultColors = ["gray", "red", "orange", "yellow", "green"];
  const defaultLabels = ["Too Short", "Weak", "Fair", "Good", "Strong"];

  const colors = customColors || defaultColors;
  const labels = customLabels || defaultLabels;

  const getStrengthLabel = (score) => labels[score] || labels[0];
  const getColor = (score) => colors[score] || colors[0];

  const barWidth = `${(score / (labels.length - 1)) * 100}%`;

  return (
    <View style={styles.container}>
      <View
        style={[
          styles.strengthBar,
          { width: barWidth, backgroundColor: getColor(score) },
        ]}
      />
      <Text style={styles.label}>Strength: {getStrengthLabel(score)}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: 10,
    borderRadius: 5,
    overflow: "hidden",
    marginTop: 10,
  },
  strengthBar: {
    height: "100%",
  },
  label: {
    marginTop: 5,
    fontWeight: "bold",
  },
});

export default PasswordStrengthMeter;
