import React, { useState } from "react";
import { View, TextInput, ScrollView, Text } from "react-native";
import PasswordStrengthMeter from "./PasswordStrengthMeter";

const PasswordStrengthMeterTest = () => {
  const [password1, setPassword1] = useState("");
  const [password2, setPassword2] = useState("");
  const [password3, setPassword3] = useState("");

  const customColors = [
    "#FFC0CB", // Soft Pink
    "#FFA07A", // Light Orange
    "#FFD700", // Vibrant Yellow
    "#32CD32", // Fresh Green
    "#0000FF", // Deep Blue
    "#800080", // Rich Purple
  ];

  const basicStrengthRules = [
    {
      test: /^(?=.*[a-z])/, // At least one lowercase letter
    },
    {
      test: /^(?=.*[A-Z])/, // At least one uppercase letter
    },
    {
      test: /^(?=.*\d)/, // At least one digit
    },
  ];

  const advancedStrengthRules = [
    {
      test: /^(?=.*[@])/, // At least one '@' symbol
    },
  ];

  // Define custom strength words state
  const customStrengthWords = [
    { word: "Too Simple", strength: 0 },
    { word: "Better", strength: 3 },
    { word: "Good", strength: 5 },
    { word: "Strong", strength: 7 },
    { word: "Very Strong", strength: 9 },
    { word: "Extremely Strong", strength: 10 },
  ];

  return (
    <ScrollView>
      <View style={{ marginVertical: 20 }}>
        <Text style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>
          Basic Strength Calculation
        </Text>
        <TextInput
          onChangeText={(text) => setPassword1(text)}
          value={password1}
          placeholder="Enter your password"
          secureTextEntry
          style={{ marginBottom: 10 }}
        />
        <PasswordStrengthMeter
          password={password1}
          strengthRules={basicStrengthRules}
          minLength={8}
        />
      </View>
      <View style={{ marginVertical: 20 }}>
        <Text style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>
          Advanced Strength Calculation
        </Text>
        <TextInput
          onChangeText={(text) => setPassword2(text)}
          value={password2}
          placeholder="Enter your password"
          secureTextEntry
          style={{ marginBottom: 10 }}
        />
        <PasswordStrengthMeter
          password={password2}
          strengthRules={advancedStrengthRules}
          minLength={3}
        />
      </View>
      <View style={{ marginVertical: 20 }}>
        <Text style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>
          Custom Words and Colors Strength Calculation
        </Text>
        <TextInput
          onChangeText={(text) => setPassword3(text)}
          value={password3}
          placeholder="Enter your password"
          secureTextEntry
          style={{ marginBottom: 10 }}
        />
        <PasswordStrengthMeter
          password={password3}
          strengthRules={advancedStrengthRules}
          colorScheme={customColors}
          strengthWords={customStrengthWords}
          showFeedbackText={true}
        />
      </View>
    </ScrollView>
  );
};

export default PasswordStrengthMeterTest;
