import React from "react";
import { View, Text, StyleSheet } from "react-native";

const PasswordStrengthMeter = ({ score, strengthLevels }) => {
  // Define default strength levels if none are provided
  const defaultStrengthLevels = [
    { label: "Very Weak", threshold: 2, color: "red" },
    { label: "Weak", threshold: 4, color: "orange" },
    { label: "Fair", threshold: 6, color: "yellow" },
    { label: "Good", threshold: 8, color: "lightgreen" },
    { label: "Strong", threshold: 10, color: "green" },
  ];

  // Use the provided strength levels or the default ones
  strengthLevels = strengthLevels || defaultStrengthLevels;

  // Calculate the strength level and bar width based on the score
  const level =
    strengthLevels
      .slice()
      .reverse()
      .find((level) => score >= level.threshold) || strengthLevels[0];
  const previousThreshold =
    strengthLevels[strengthLevels.indexOf(level) - 1]?.threshold || 0;
  const range = level.threshold - previousThreshold;
  const relativeScore = score - previousThreshold;
  const barWidth = range ? `${(relativeScore / range) * 100}%` : "100%"; // Calculate the width relative to the current range

  console.log(`Score: ${score}, Level: ${level.label}, Bar Width: ${barWidth}`);

  return (
    <View style={styles.container}>
      <View
        style={[
          styles.strengthBar,
          { width: barWidth, backgroundColor: level.color },
        ]}
      />
      <Text style={styles.label}>Strength: {level.label}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: 10,
    borderRadius: 5,
    overflow: "hidden",
    marginTop: 10,
  },
  strengthBar: {
    height: "100%",
  },
  label: {
    marginTop: 5,
    fontWeight: "bold",
  },
});

export default PasswordStrengthMeter;
