import React, { useState, useEffect } from "react";
import { View, Text, StyleSheet } from "react-native";

const PasswordStrengthMeter = ({
  password,
  calculateStrength,
  colorScheme = [
    "#FFC0CB",
    "#FFA07A",
    "#FFD700",
    "#32CD32",
    "#0000FF",
    "#800080",
  ], // Default colors
  strengthWords = [
    // Default strength words
    { word: "Weak", strength: 0 },
    { word: "Better", strength: 4 },
    { word: "Strong", strength: 8 },
  ],
  barWidth = "100%",
  showStrengthBar = true,
  showFeedbackText = true,
}) => {
  const [strengthLabel, setStrengthLabel] = useState(strengthWords[0].word);

  useEffect(() => {
    const newStrengthScore = calculateStrength
      ? calculateStrength(password)
      : defaultStrengthCalculation(password);
    const wordObj = strengthWords.find(
      (obj) => newStrengthScore >= obj.strength
    );
    setStrengthLabel(wordObj ? wordObj.word : "Weak");
  }, [password, calculateStrength, strengthWords]);

  const normalizedStrength = Math.min(
    Math.max(strengthLabel.strength, 0),
    colorScheme.length - 1
  );
  const barColor = colorScheme[normalizedStrength];

  const styles = StyleSheet.create({
    container: {
      width: barWidth,
      height: 10,
      backgroundColor: "#eee",
      borderRadius: 5,
      overflow: "hidden",
      marginTop: 10,
    },
    strengthBar: {
      width: `${(normalizedStrength / (colorScheme.length - 1)) * 100}%`,
      height: "100%",
      backgroundColor: barColor,
    },
    label: {
      marginTop: 5,
      fontWeight: "bold",
    },
    feedbackText: {
      marginTop: 5,
      color: "gray",
    },
  });

  return (
    <View>
      {showStrengthBar && (
        <View style={styles.container}>
          <View style={styles.strengthBar} />
        </View>
      )}
      <Text style={styles.label}>Strength: {strengthLabel}</Text>
      {showFeedbackText && (
        <Text style={styles.feedbackText}>
          Tips to improve your password strength.
        </Text>
      )}
    </View>
  );
};

const defaultStrengthCalculation = (password) => {
  let strengthPoints = 0;

  if (!password) {
    return strengthPoints;
  }

  if (password.length >= 8) strengthPoints += 2;
  if (password.length >= 12) strengthPoints += 2;
  if (/[a-z]/.test(password)) strengthPoints += 1;
  if (/[A-Z]/.test(password)) strengthPoints += 1;
  if (/\d/.test(password)) strengthPoints += 1;
  if (/[^A-Za-z0-9]/.test(password)) strengthPoints += 2;

  return strengthPoints;
};

export default PasswordStrengthMeter;
