import React, { useState } from "react";
import { View, TextInput, Text, StyleSheet } from "react-native";
import PropTypes from "prop-types";

const PasswordStrengthMeter = ({
  score,
  customStrengthLevels,
  style,
  onPasswordChange,
}) => {
  const [password, setPassword] = useState("");

  const handlePasswordChange = (text) => {
    setPassword(text);
    onPasswordChange(text);
  };

  const getLevelInfo = () => {
    for (let i = 0; i < levels.length; i++) {
      if (score <= levels[i].threshold || i === levels.length - 1) {
        const prevThreshold = i === 0 ? 0 : levels[i - 1].threshold;
        const range = levels[i].threshold - prevThreshold;
        const scoreInCurrentRange = score - prevThreshold;
        const barWidthPercentage = (scoreInCurrentRange / range) * 100;
        return {
          label: levels[i].label,
          color: levels[i].color,
          barWidth: `${Math.min(barWidthPercentage, 100)}%`,
        };
      }
    }
  };

  const levels = customStrengthLevels || defaultStrengthLevels;
  const { label, color, barWidth } = getLevelInfo();

  return (
    <View style={[styles.container, style]}>
      <TextInput
        style={styles.input}
        placeholder="Enter your password"
        secureTextEntry={true}
        onChangeText={handlePasswordChange}
        value={password}
      />
      <View
        style={[
          styles.progressBar,
          { backgroundColor: color, width: barWidth },
        ]}
      />
      <Text style={[styles.label, { color: color }]}>{label}</Text>
    </View>
  );
};

const defaultStrengthLevels = [
  { label: "Very Weak", threshold: 10, color: "red" },
  { label: "Weak", threshold: 30, color: "orange" },
  { label: "Average", threshold: 50, color: "yellow" },
  { label: "Strong", threshold: 70, color: "lightgreen" },
  { label: "Very Strong", threshold: 100, color: "green" },
];

PasswordStrengthMeter.propTypes = {
  score: PropTypes.number.isRequired,
  customStrengthLevels: PropTypes.arrayOf(
    PropTypes.shape({
      label: PropTypes.string,
      threshold: PropTypes.number,
      color: PropTypes.string,
    })
  ),
  style: PropTypes.object,
  onPasswordChange: PropTypes.func.isRequired,
};

PasswordStrengthMeter.defaultProps = {
  customStrengthLevels: defaultStrengthLevels,
  style: {},
};

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    padding: 16,
    borderRadius: 8,
    width: "100%",
  },
  input: {
    height: 30,
    width: "100%",
    borderColor: "gray",
    borderWidth: 1,
    borderRadius: 4,
    paddingHorizontal: 8,
    marginBottom: 8,
  },
  label: {
    marginTop: 5,
    fontWeight: "bold",
  },
  progressBar: {
    height: 10,
    borderRadius: 5,
    marginTop: 8,
    marginBottom: 8,
    backgroundColor: "lightgrey",
    alignSelf: "stretch",
  },
});

export default PasswordStrengthMeter;
