// PasswordStrengthMeterTest.js
import React, { useState } from "react";
import { View, TextInput, ScrollView, Text } from "react-native";
import PasswordStrengthMeter from "./PasswordStrengthMeter";

const PasswordStrengthMeterTest = () => {
  const [password1, setPassword1] = useState("");
  const [password2, setPassword2] = useState("");
  const [password3, setPassword3] = useState("");

  const customColors = [
    "#FFC0CB", // Soft Pink
    "#FFA07A", // Light Orange
    "#FFD700", // Vibrant Yellow
    "#32CD32", // Fresh Green
    "#0000FF", // Deep Blue
    "#800080", // Rich Purple
  ];

  const basicStrengthAlgorithm = (password) => {
    return password.length; // Basic example: Length-based strength calculation
  };

  const advancedStrengthAlgorithm = (password) => {
    let strength = 0;
    if (password.length >= 8) strength += 1;
    if (/[A-Z]/.test(password)) strength += 1;
    if (/\d/.test(password)) strength += 1;
    if (/[^A-Za-z0-9]/.test(password)) strength += 1;
    return strength;
  };

  // Define custom strength algorithm function
  const customStrengthAlgorithm = (password, customStrengthWords) => {
    // Implement your custom strength calculation logic here
    // You can use customStrengthWords to determine the strength label
    // and customColors for the bar color
    // For example, you can check the length of the password and assign a label accordingly
    let strength = 0;
    if (password.length <= 4) {
      strength = 0;
    } else if (password.length <= 8) {
      strength = 3;
    } else {
      strength = 5;
    }

    // Find the corresponding strength word and color
    const wordObj = customStrengthWords.find(
      (wordObj) => strength >= wordObj.strength
    );

    return {
      score: strength,
      label: wordObj ? wordObj.word : "Unknown",
      color: customColors[wordObj ? wordObj.word : "Unknown"],
    };
  };

  // Define custom strength words state
  const customStrengthWords = [
    { word: "Too Simple", strength: 0 },
    { word: "Better", strength: 3 },
    { word: "Good", strength: 5 },
    { word: "Strong", strength: 7 },
    { word: "Very Strong", strength: 9 },
    { word: "Extremely Strong", strength: 10 },
  ];

  return (
    <ScrollView>
      {/* Basic Strength Calculation */}
      <View style={{ marginVertical: 20 }}>
        <Text style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>
          Basic Strength Calculation
        </Text>
        <TextInput
          onChangeText={(text) => setPassword1(text)}
          value={password1}
          placeholder="Enter your password"
          secureTextEntry
          style={{ marginBottom: 10 }}
        />
        <PasswordStrengthMeter
          password={password1}
          customCriteria={basicStrengthAlgorithm} // Use customCriteria prop
        />
      </View>

      {/* Advanced Strength Calculation */}
      <View style={{ marginVertical: 20 }}>
        <Text style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>
          Advanced Strength Calculation
        </Text>
        <TextInput
          onChangeText={(text) => setPassword2(text)}
          value={password2}
          placeholder="Enter your password"
          secureTextEntry
          style={{ marginBottom: 10 }}
        />
        <PasswordStrengthMeter
          password={password2}
          customCriteria={advancedStrengthAlgorithm} // Use customCriteria prop
        />
      </View>

      {/* Custom Words and Colors Strength Calculation */}
      <View style={{ marginVertical: 20 }}>
        <Text style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>
          Custom Words and Colors Strength Calculation
        </Text>
        <TextInput
          onChangeText={(text) => setPassword3(text)}
          value={password3}
          placeholder="Enter your password"
          secureTextEntry
          style={{ marginBottom: 10 }}
        />
        <PasswordStrengthMeter
          password={password3}
          colorScheme={customColors}
          customCriteria={(password) =>
            customStrengthAlgorithm(password, customStrengthWords)
          } // Use customCriteria prop
          showFeedbackText={true}
        />
      </View>
    </ScrollView>
  );
};

export default PasswordStrengthMeterTest;
