import React, { useState, useEffect } from "react";
import { View, Text, StyleSheet } from "react-native";

const defaultStrengthCalculation = (password) => {
  let strengthPoints = 0;

  if (!password) {
    return strengthPoints;
  }

  if (password.length >= 8) strengthPoints += 2;
  if (password.length >= 12) strengthPoints += 2;
  if (/[a-z]/.test(password)) strengthPoints += 1;
  if (/[A-Z]/.test(password)) strengthPoints += 1;
  if (/\d/.test(password)) strengthPoints += 1;
  if (/[^A-Za-z0-9]/.test(password)) strengthPoints += 2;

  return strengthPoints;
};

const PasswordStrengthMeter = ({
  password,
  calculateStrength,
  colorScheme = [],
  labels = [],
  strengthWords,
  barWidth = "100%",
  showStrengthBar = true,
  showFeedbackText = true,
}) => {
  const [strengthScore, setStrengthScore] = useState(0);

  useEffect(() => {
    const newStrengthScore = calculateStrength
      ? calculateStrength(password)
      : defaultStrengthCalculation(password);
    setStrengthScore(newStrengthScore);
  }, [password, calculateStrength]);

  const getStrengthLabel = () => {
    const wordObj = strengthWords.find(
      (wordObj) => strengthScore >= wordObj.strength
    );
    return wordObj ? wordObj.word : "Weak"; // Default label if no match is found
  };

  const strengthLabel = getStrengthLabel();
  const normalizedStrength = Math.min(
    Math.max(strengthScore, 0),
    colorScheme.length - 1
  );
  const barColor = colorScheme[normalizedStrength] || "#ddd"; // Fallback color

  const styles = StyleSheet.create({
    container: {
      width: barWidth,
      height: 10,
      backgroundColor: "#eee",
      borderRadius: 5,
      overflow: "hidden",
      marginTop: 10,
    },
    strengthBar: {
      width: `${(strengthScore / (strengthWords.length - 1)) * 100}%`,
      height: "100%",
      backgroundColor: barColor,
    },
    label: {
      marginTop: 5,
      fontWeight: "bold",
    },
    feedbackText: {
      marginTop: 5,
      color: "gray",
    },
  });

  return (
    <View>
      {showStrengthBar && (
        <View style={styles.container}>
          <View style={styles.strengthBar} />
        </View>
      )}
      <Text style={styles.label}>Strength: {strengthLabel}</Text>
      {showFeedbackText && (
        <Text style={styles.feedbackText}>
          Tips to improve your password strength.
        </Text>
      )}
    </View>
  );
};

export default PasswordStrengthMeter;
