import React from "react";
import { View, Text, StyleSheet } from "react-native";

const PasswordStrengthMeter = ({ score, strengthLevels }) => {
  const findLevel = (score) => {
    return (
      strengthLevels.find((level) => score <= level.threshold) ||
      strengthLevels[strengthLevels.length - 1]
    );
  };

  const level = findLevel(score);
  const barWidth = `${(score / level.threshold) * 100}%`;

  return (
    <View style={styles.container}>
      <View
        style={[
          styles.strengthBar,
          { width: barWidth, backgroundColor: level.color },
        ]}
      />
      <Text style={styles.label}>Strength: {level.label}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: 10,
    borderRadius: 5,
    overflow: "hidden",
    marginTop: 10,
  },
  strengthBar: {
    height: "100%",
  },
  label: {
    marginTop: 5,
    fontWeight: "bold",
  },
});

export default PasswordStrengthMeter;
